INSERT INTO `categories` (`id`, `name`, `description`, `created_at`, `updated_at`) VALUES
(3, 'Thuốc gây tê', 'Có thể dùng để tiêm hoặc dùng ngoài', '2023-05-01 02:55:33', '2023-05-30 21:05:53'),
(4, 'Thuốc gây mê', 'Thuốc gây mê là thuật ngữ y học để chỉ các thuốc được sử dụng trước, trong khi phẫu thuật, thủ thuật để đảm bảo bệnh nhân không có cảm giác đau trong suốt quá trình đó. Một số trường hợp, thuốc khiến bệnh nhân như giống như khi ngủ và khi thoát mê tỉnh dậy sẽ không nhớ gì đã xảy ra.', '2023-05-30 21:06:02', '2023-08-06 08:24:28'),
(5, 'Thuốc giãn cơ', 'Thuốc giãn cơ là thuốc có công dụng điều trị chuột rút, co thắt cơ, .... Các loại thuốc giãn cơ thường được kê đơn để hỗ trợ làm dịu cơn đau và khó chịu do co thắt cơ không tử chủ, gây tình trạng căng cơ quá mức và thường liên quan đến đau lưng dưới, đau cổ.', '2023-05-30 21:06:14', '2023-08-06 08:24:09'),
(6, 'Thuốc giảm đau', 'Thuốc giảm đau chính là một trong những phát minh quan trọng của ngành y học hiện đại. Nhờ có thuốc giảm đau chúng ta đã không còn phải chịu đựng những cơn đau thể xác do bệnh tật gây ra.', '2023-05-30 21:06:29', '2023-08-06 08:23:47'),
(7, 'Thuốc hạ sốt', 'Thuốc hạ sốt là loại thuốc khá phổ biến, được dùng cho cả người lớn và trẻ em. Thường được sử dụng để điều trị các triệu chứng như: Sốt, đau đầu, đau cơ, đau khớp, đau lưng, đau răng, cảm sốt, viêm khớp nhẹ...', '2023-05-30 21:06:37', '2023-08-06 08:20:14'),
(8, 'Thuốc chống viêm', 'Thuốc chống viêm không steroid hoạt động bằng cách ngăn chặn các prostaglandin -là những chất làm tăng độ nhạy cảm của các đầu dây thần kinh của bạn và tăng cường cơn đau trong quá trình viêm. Ngoài ra prostaglandin cũng đóng một vai trò trong việc kiểm soát nhiệt độ cơ thể của bạn.', '2023-05-30 21:06:52', '2023-08-06 08:19:15'),
(9, 'Thuốc điều trị gút', 'Bệnh Gout là bệnh viêm khớp thường gặp nguyên nhân do rối loạn chuyển hóa Purin. Điều này dẫn đến tích tụ urat dẫn đến viêm và đau khớp. Một số loại thuốc điều trị Gout giúp giảm nhanh triệu chứng và dự phòng đợt Gout cấp tính.', '2023-05-30 21:07:16', '2023-08-06 08:18:48'),
(10, 'Thuốc chống thoái hóa khớp', 'Bệnh thoái hóa khớp gối là bệnh mạn tính chủ yếu xảy ra do quá trình lão hóa và các tác nhân đẩy nhanh quá trình lão hóa như: Chấn thương, lao động nặng, chế độ dinh dưỡng không hợp lý.', '2023-05-30 21:07:30', '2023-08-06 08:18:22'),
(11, 'Thuốc chống dị ứng', 'Các nhóm thuốc chống dị ứng, bao gồm cả thuốc không kê đơn và kê đơn, giúp giảm bớt, điều trị các triệu chứng dị ứng khó chịu như nghẹt mũi và sổ mũi. Các nhóm thuốc dị ứng bao gồm corticosteroid, thuốc kháng histamine, thuốc thông mũi, thuốc kết hợp và các loại khác.', '2023-05-30 21:07:54', '2023-08-06 08:17:59'),
(12, 'Thuốc giải độc', 'Thuốc giải độc gan là một loại thuốc nhằm loại bỏ độc tố trong cơ thể, giúp bạn giảm cân hoặc cải thiện sức khỏe.', '2023-05-30 21:08:12', '2023-08-06 08:17:47'),
(13, 'Thuốc chống co giật', 'Co giật là hoạt động điện bất thường trong não diễn ra một cách nhanh chóng. Trong trường hợp nghiêm trọng, nó có thể gây bất tỉnh và co giật, khi cơ thể bạn run rẩy không kiểm soát được. Các cơn co giật thường đến đột ngột.', '2023-05-30 21:08:56', '2023-08-06 08:05:15'),
(14, 'Thuốc động kinh', 'Thuốc chống động kinh (còn được gọi là thuốc chống co giật) được dùng rất phổ biến với nhiều chủng lại phong phú, đa dạng. Điều quan trọng nhất là lựa chọn thuốc điều trị bệnh động kinh sao cho phù hợp với tình trạng bệnh, khả năng kinh tế của bệnh nhân, giúp cải thiện chất lượng sống của người bệnh và giảm thiểu các tác dụng phụ có hại của thuốc.', '2023-05-30 21:09:05', '2023-08-06 08:04:33'),
(15, 'Thuốc kháng sinh', 'Thuốc kháng sinh là cái tên không còn quá xa lạ với tất cả mọi người. Loại thuốc quen mặt này xuất hiện trong điều trị nhiều bệnh lý, từ đơn giản đến phức tạp và có thể mua ở hầu hết mọi nhà thuốc. Tuy nhiên, liệu bạn đã thật sự hiểu rõ về loại thuốc này? Hãy cùng tìm hiểu rõ hơn về lợi ích và nguy cơ mà nó mang lại đối với sức khỏe người bệnh.', '2023-05-30 21:09:18', '2023-08-06 08:03:45'),
(16, 'Thuốc điều trị ký sinh trùng', 'Trong cuộc sống hàng ngày, chúng ta có thể bị nhiễm ký sinh trùng từ nhiều nguồn khác nhau, bao gồm thức ăn, nước uống và môi trường xung quanh chúng ta. Những ký sinh trùng này có thể gây ra các vấn đề sức khỏe và bệnh tật nghiêm trọng. May mắn thay, đã có nhiều loại thuốc trị ký sinh trùng ở người được phát triển để giúp chúng ta chống lại các bệnh tật này.', '2023-05-30 21:09:36', '2023-08-06 08:03:09'),
(17, 'Thuốc điều trị đau nửa đầu', 'Đau nửa đầu Migraine là bệnh lý khá phổ biến, tạo nên gánh nặng bệnh tật đáng kể. Tỷ lệ hiện mắc của căn bệnh này hàng năm là 18% ở phụ nữ và 6% đối với nam giới, độ tuổi thường hay gặp nhất là 25 – 55.', '2023-05-30 21:10:00', '2023-08-06 08:02:41'),
(18, 'Thuốc điều trị ung thư', NULL, '2023-05-30 21:10:11', '2023-05-30 21:10:11'),
(19, 'Thuốc điều hoà miễn dịch', 'Thuốc điều biến miễn dịch tại chỗ là các chất có tác dụng điều chỉnh đáp ứng miễn dịch cục bộ của da. Hiện nay, đây là thuốc được lựa chọn trong điều trị các bệnh lý liên quan đến hệ miễn dịch như viêm da cơ địa và viêm da tiếp xúc dị ứng, rụng tóc từng vùng, bệnh vẩy nến, bạch biến, rối loạn mô liên kết như xơ cứng bì khư trú morphea và lupus ban đỏ.', '2023-05-30 21:10:31', '2023-08-06 08:02:20'),
(20, 'Thuốc tiền mê', 'Thuốc tiền mê là thuốc được dùng cho người bệnh trước khi gây mê để tiến hành phẫu thuật.', '2023-06-04 00:22:35', '2023-08-06 08:01:31'),
(21, 'Thuốc an thần', 'Thuốc an thần là tên gọi chung để nói về các nhóm thuốc, thực phẩm chức năng dùng để điều hòa sự ổn định của trung ương thần kinh, giảm cảm giác căng thẳng, mệt mỏi do áp lực công việc, cuộc sống.', '2023-06-04 00:22:54', '2023-08-06 08:01:14'),
(22, 'Thuốc chăm sóc giảm nhẹ', NULL, '2023-06-04 00:23:50', '2023-06-04 00:23:50'),
(23, 'Thuốc chống động kinh', 'Thuốc chống động kinh (còn được gọi là thuốc chống co giật) được dùng rất phổ biến với nhiều chủng lại phong phú, đa dạng. Điều quan trọng nhất là lựa chọn thuốc điều trị bệnh động kinh sao cho phù hợp với tình trạng bệnh, khả năng kinh tế của bệnh nhân, giúp cải thiện chất lượng sống của người bệnh và giảm thiểu các tác dụng phụ có hại của thuốc.', '2023-06-04 00:25:13', '2023-08-06 08:00:47'),
(24, 'Thuốc trị ký sinh trùng', NULL, '2023-06-04 00:25:32', '2023-06-04 00:25:32'),
(25, 'Thuốc chữa bệnh phong', 'Bệnh phong là bệnh nhiễm khuẩn Mycobacterium leprae. Nếu được điều trị sớm và đúng, bệnh phong có thể khỏi, không để lại di chứng. Hiện nay, ba thuốc chủ yếu điều trị phong là: dapson, rifampicin và clofazimin.', '2023-06-04 00:26:06', '2023-08-06 07:59:43'),
(26, 'Thuốc chữa bệnh lao', 'Bệnh lao do vi khuẩn gọi là Mycobacterium tuberculosis gây ra. Vi khuẩn có khả năng lây lan từ người sang người qua các giọt nhỏ li ti trong không khí. Điều này xảy ra khi một người mắc bệnh lao ở dạng hoạt động nhưng chưa được điều trị bệnh lao mà vẫn vô tư ho, nói chuyện, hắt hơi, khạc nhổ, ca hát nơi công cộng.', '2023-06-04 00:26:19', '2023-08-06 07:59:29'),
(27, 'Thuốc chống nấm', 'Nấm có mặt khắp nơi trong đất, nước, không khí, trên các bề mặt. Cơ thể có thể bị nhiễm nấm bằng nhiều cách khác nhau như nhiễm nấm trực tiếp qua da, nhiễm qua thức ăn, qua niêm mạc, qua đường hô hấp,... Có rất nhiều nấm được phát hiện có thể gây bệnh cho cơ thể, trong đó thường gặp nhất là các loại nấm gây các bệnh về da, niêm mạc, nhiễm nấm nội tạng,...', '2023-06-04 00:26:35', '2023-08-06 07:59:08'),
(28, 'Thuốc điều trị amip', 'Bệnh do amip do vi khuẩn Entamoeba histolytica gây ra, lây truyền qua đường phân, miệng và thường lây nhiễm khi ăn phải các bào nang ở nước hoặc thực phẩm bị ô nhiễm.', '2023-06-04 00:26:58', '2023-08-06 07:58:33'),
(29, 'Thuốc điều tri bệnh sốt rét', 'Sốt rét là căn bệnh nguy hiểm, thậm chí đe dọa tới tính mạng bệnh nhân nếu không được phát hiện, điều trị sớm. Bên cạnh đó, vì các thuốc điều trị bệnh sốt rét có thể gây ra tác dụng phụ nguy hiểm nên người bệnh không được tự ý sử dụng mà cần tuân thủ đúng chỉ định của bác sĩ.', '2023-06-04 00:27:07', '2023-08-06 07:54:31'),
(30, 'Thuốc chống virus herpes', 'Thuốc Acyclovir là một trong số các loại thuốc kháng virus, nó có tác dụng làm chậm sự phát triển và lây lan của virus, tạo điều kiện thuận lợi cho cơ thể chống lại nhiễm trùng.\n\nThuốc Acyclovir được sử dụng trong điều trị các bệnh: thủy đậu, bệnh zona thần kinh, herpes sinh dục, vết loét lạnh. Ngoài ra, thuốc Acyclovir còn được sử dụng với nhiều nhiều mục đích khác, tùy theo tình trạng bệnh và chỉ định của bác sĩ.', '2023-06-04 00:27:19', '2023-08-06 07:53:59'),
(31, 'Thuốc ức chế protease', 'Saquinavir là thuốc ức chế protease của HIV (HIV-1 và HIV-2). Thuốc được dùng kết hợp với các thuốc kháng retrovirus khác để điều trị nhiễm HIV. Saquinavir ức chế đặc hiệu lên protease của HIV (mạnh gấp 50.000 lần so với protease của người) và không bền vững.', '2023-06-04 00:27:30', '2023-08-06 07:53:33'),
(32, 'Thuốc kết hợp liều cố định', 'Thuốc phối hợp cố định liều (Fixed dose drug combinations: FDCs) là các chế phẩm phối hợp hai hay nhiều hoạt chất trong một dạng bào chế thông thường. Hầu hết các thuốc được bào chế dưới dạng đơn thành phần.', '2023-06-04 00:27:40', '2023-08-06 07:53:13'),
(33, 'Thuốc điều trị cơn đau cấp', 'Thuốc giảm đau nhẹ bao gồm các NSAID và acetaminophen (paracetamol), các thuốc này được dùng rất phổ biến trong điều trị các cơn đau cấp tính vì hướng đến mục đích giảm tình trạng viêm tự nhiên do chấn thương.', '2023-06-04 00:27:51', '2023-08-06 07:52:44'),
(34, 'Thuốc phòng bệnh', NULL, '2023-06-04 00:27:58', '2023-06-04 00:27:58'),
(35, 'Thuốc ức chế miễn dịch', 'Thuốc ức chế miễn dịch là những thuốc ức chế hoặc ngăn chặn hoạt động của hệ thống miễn dịch. Chúng được sử dụng trong liệu pháp ức chế miễn dịch để: 1) Ngăn chặn sự từ chối của các cơ quan và mô cấy ghép (ví dụ: tủy xương, tim, thận, gan) 2) Điều trị các bệnh tự miễn hoặc các bệnh có khả năng có nguồn gốc tự miễn (ví dụ: viêm khớp dạng thấp, xơ cứng rải rác, bệnh nhược cơ, bệnh vẩy nến, bạch biến, lupus ban đỏ hệ thống, bệnh Crohn, bệnh Behçet, và viêm loét đại tràng). 3) Điều trị một số bệnh viêm không tự miễn khác (ví dụ: hen phế quản), viêm cột sống dính khớp.', '2023-06-04 00:28:08', '2023-08-06 07:51:50'),
(36, 'Thuốc chống ung thư', NULL, '2023-06-04 00:28:15', '2023-06-04 00:28:15'),
(37, 'Thuốc chống parkinson', 'Levodopa là tiêu chuẩn vàng của liệu pháp thay thế dopamine trong bệnh Parkinson. Nó được chỉ định kèm với một chất ức chế dopa decarboxylase (carbidopa) để giảm sự phân hủy ngoại biên của nó và giảm buồn nôn. Levodopa đặc biệt hiệu quả trong điều trị không động và cứng, còn tác dụng biến động khi điều trị run. Nghiên cứu lâm sàng gợi ý rằng điều trị levodopa không làm nặng thêm tiến triển bệnh.', '2023-06-04 00:30:32', '2023-08-06 07:51:15'),
(38, 'Thuốc chống thiếu máu', 'Thiếu máu là do cơ thể bị thiếu hụt hemoglobin hay các tế bào hồng cầu. Hiện tượng này sẽ làm giảm lưu lượng oxy được vận chuyển đến các mô, cơ cũng như những cơ quan khác. Khi một người bị thiếu máu thường sẽ có các dấu hiệu như khó thở, choáng váng, suy nhược, chóng mặt và mệt mỏi.', '2023-06-04 00:30:49', '2023-08-06 07:51:03'),
(39, 'Thuốc chống đau thắt ngực', 'Đau thắt ngực là cơn đau xuất phát từ tim do nhiều nguyên nhân khác nhau mà chủ yếu do xơ vữa động mạch, co thắt động mạch vành... Chữa đau thắt ngực nhằm làm giảm đau nhanh chóng, hạn chế các mảng xơ vữa tích tụ thêm, làm chậm tiến triển của bệnh động thời giảm nguy cơ nhồi máu cơ tim đối với người bệnh.', '2023-06-04 00:31:16', '2023-08-06 07:49:48'),
(40, 'Thuốc chống loạn nhịp', 'Nhịp tim nhanh bất thường có triệu chứng trống ngực, hồi hộp, mệt mỏi… và ngoại tâm thu có thể được điều trị bằng nhiều loại thuốc chống loạn nhịp. Một số loại thuốc dạng tiêm tĩnh mạch được dùng trong tình trạng khẩn cấp. Thuốc uống được sử dụng trong điều trị lâu dài. Những loại thuốc này ngăn chặn sự xuất hiện xung điện bất thường trong tim, giúp làm giảm nhịp tim.', '2023-06-04 00:31:38', '2023-08-06 07:49:16'),
(41, 'Thuốc điều trị tăng huyết áp', 'Phần lớn tăng huyết áp là không rõ nguyên nhân (tăng nguyên phát), chỉ có 10% các trường hợp có nguyên nhân rõ ràng (tăng thứ phát). Tăng huyết áp là bệnh mạn tính cần được theo dõi suốt đời, điều trị đúng và đủ mỗi ngày, lâu dài bằng thuốc hạ huyết áp sao cho đạt được mục tiêu là < 140/90 mmHg hoặc có thể về thấp hơn nữa nếu người bệnh vẫn dung nạp được.', '2023-06-04 00:32:01', '2023-08-06 07:48:54'),
(42, 'Thuốc điều trị hạ huyết áp', 'Huyết áp được tạo ra khi dòng máu lưu thông tác động một áp lực lên thành động mạch. Sự tuần hoàn máu sẽ do tim co bóp và đẩy đi khắp hệ thống tuần hoàn để cung cấp máu đến mọi cơ quan trong cơ thể. Đơn vị đo huyết áp là mmHg, được chia thành 2 chỉ số huyết áp tâm trương và huyết áp tâm thu. 2 chỉ số này có giới hạn bình thường ở mức 60 - 90 mmHg (huyết áp tâm trương) và 90 - 140 mmHg (huyết áp tâm thu).', '2023-06-04 00:32:10', '2023-08-06 07:48:30'),
(43, 'Thuốc điều trị suy tim', 'Suy tim cần phải điều trị suốt đời bằng cách sử dụng thuốc và thay đổi lối sống. Có rất nhiều nhóm thuốc điều trị suy tim, trong đó mỗi nhóm đều có những ưu và nhược điểm riêng. Tùy vào từng mức độ suy tim, các bệnh lý đi kèm, độ tuổi mà có phác đồ điều trị khác nhau.', '2023-06-04 00:32:18', '2023-08-06 07:48:09'),
(44, 'Thuốc chống huyết khối', 'Các thuốc chống đông máu nhóm Heparin trọng lượng phân tử thấp hoặc trung bình có thể gây ra tác dụng nhanh hoặc chậm tùy vào trọng lượng phân tử của thuốc. Với khả năng tạo ra tác dụng nhanh chóng, các thuốc chống đông máu nhóm Heparin được dùng trong điều trị và dự phòng các bệnh như thuyên tắc phổi, chạy thận nhân tạo, huyết khối tĩnh mạch sâu chi dưới và hội chứng mạch vành cấp.', '2023-06-04 00:32:27', '2023-08-06 07:47:15'),
(45, 'Thuốc hạ lipit máu', 'Thuốc hạ cholesterol máu nhóm statin có tác dụng giảm cholesterol trong máu nhờ ức chế enzym Hydroxymethylglutaryl CoA Reductase, đây là enzym giúp tổng hợp cholesterol toàn phần, từ đó làm giảm cholesterol toàn phần nội sinh, kích thích tăng các thụ thể LDL-c (cholesterol LDL).', '2023-06-04 00:32:37', '2023-08-06 07:44:42'),
(46, 'Thuốc chống viêm, ngứa', 'Thuốc không kê đơn (OTC) là những loại thuốc bạn có thể mua mà không cần đơn của bác sĩ. Thuốc chống viêm không steroid (NSAID) là những loại thuốc giúp giảm viêm và thường giúp giảm đau.', '2023-06-04 00:33:05', '2023-08-06 07:43:42'),
(47, 'Thuốc trị ghẻ', 'Ghẻ ngứa hay ghẻ nước là một bệnh lý  về da liễu gây ra bởi sự xâm nhập của ký sinh trùng. Ghẻ nước khiến trên da nổi nhiều mụn nước, gây ra cảm giác ngứa ngáy vô cùng khó chịu. Cảm giác này càng tăng lên nếu những chiếc mụn ghẻ bị vỡ khiến chất dịch bên trong thoát ra ngoài. Người bị ghẻ ngứa nên đi khám chuyên khoa da liễu để được thăm khám và kê đơn thuốc phù hợp, tránh trường hợp để lâu gây nhiễm trùng vùng da bị mụn nước.', '2023-06-04 00:33:15', '2023-07-04 23:17:00');
